def union(S):
    #recives a list with elemnts as lists and returns a list of non list elements 
    SU = []
    l=0
    i=0
    
    while i<len(S):
        l=0
        while l<len(S[i]):
            SU.append(S[i][l])
            l=l+1
        i=i+1
    i=0
    l=0
    indx = []
    while i<len(SU):
        l=0
        while l+i+1<len(SU):
            if SU[i] == SU[l+i+1]:
                SU.remove(SU[l+i+1])
            else:
                l=l+1
        i=i+1
    return SU

